import React, { useEffect } from 'react'
import { StyleSheet, Text, View, Image, TouchableOpacity } from 'react-native'
import { Input, Icon } from 'react-native-elements';


const RegisterTemplate = ({ navigation, registerScreen, setRegisterScreen, imgSrc, headingText1, headingText2, inputLabel, inputPlaceholder }) => {
    let a = setRegisterScreen;
    return (
        <View style={styles.mainContainer}>
            <View style={styles.navigationContainer}>
                <View style={styles.navigationRow}>
                    <Icon
                        name='arrow-back'
                        type='material'
                        color='#000'
                        iconStyle={{ display: (registerScreen !== 1) ? 'none' : 'flex' }}
                        onPress={() => console.log('back')} />
                    <TouchableOpacity onPress={() => { c }}><Text>Continue</Text></TouchableOpacity>
                </View>
            </View>
            <View style={styles.imageContainer}>
                <Image style={styles.image} source={imgSrc} />
            </View>
            <View style={styles.headerContainer}>
                <Text style={styles.headerText1}>{headingText1}</Text>
                <Text style={styles.headerText2}>{headingText2}</Text>
            </View>
            <View style={styles.inputContainer}>
                <Input
                    label={inputLabel}
                    labelStyle={{ paddingBottom: '3%', paddingLeft: '1%' }}
                    placeholder={inputPlaceholder}
                    inputContainerStyle={{ paddingLeft: '3%', borderColor: 'rgba(0,0,0,0.6)', borderWidth: 1, borderBottomWidth: 1, borderRadius: 10 }}
                    containerStyle={{ width: '90%' }}
                // errorMessage='invalid email'
                />
            </View>
            <View style={styles.buttonContainer}>
                <TouchableOpacity style={styles.loginButton} onPress={() => { }}>
                    <Text style={styles.loginButtonText}>Continue</Text>
                </TouchableOpacity>
            </View>
        </View>
    )
}

export default RegisterTemplate

const styles = StyleSheet.create({
    mainContainer: {
        // backgroundColor: 'yellow',
        flex: 1
    },
    navigationContainer: {
        flex: 0.1,
        // backgroundColor: 'red',
        alignItems: 'center',
        justifyContent: 'flex-end'
    },
    navigationRow: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        width: '90%'
    },
    imageContainer: {
        // backgroundColor: 'purple',
        flex: 0.15,
        paddingLeft: '10%'
    },
    image: {
        // backgroundColor: 'yellow',
        flex: 1,
        width: '30%',
        resizeMode: 'contain'
    },
    headerContainer: {
        // backgroundColor: 'pink',
        flex: 0.08,
        justifyContent: 'center'
    },
    headerText1: {
        fontSize: 25,
        fontWeight: '700',
        color: 'rgba(0,0,0,0.7)',
        paddingLeft: '5%'
    },
    headerText2: {
        fontSize: 15,
        fontWeight: '300',
        color: 'rgba(0,0,0,0.7)',
        paddingLeft: '6%'
    },
    inputContainer: {
        // backgroundColor: 'green',
        flex: 0.4,
        alignItems: 'center',
        paddingTop: '7%'
    },
    buttonContainer: {
        // backgroundColor: 'yellow',
        flex: 0.23,
        justifyContent: 'flex-end',
        alignItems: 'center'
    },
    loginButton: {
        width: '85%',
        height: '30%',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 8,
        backgroundColor: 'rgba(0,0,0,0.2)',
    },
    loginButtonText: {
        color: 'white',
        fontSize: 22
    }
})
