import React, { useState, useEffect } from 'react';
import { View, Text, Image, ImageBackground, StyleSheet, TouchableOpacity, Keyboard } from 'react-native';
import { Input } from 'react-native-elements';


const LoginScreen = ({navigation}) => {
    let [keyboardOpen, setKeyboardOpen] = useState(false);

    useEffect(() => {
        Keyboard.addListener('keyboardDidShow', () => { setKeyboardOpen(true) });
        Keyboard.addListener('keyboardDidHide', () => { setKeyboardOpen(false) });

    }, []);

    return (
        <View enabled={false} style={styles.mainContainer}>
            <ImageBackground style={keyboardOpen ? styles.bannerContainer_hide : styles.bannerContainer} source={require('../../../assets/login-banner.jpg')} resizeMode="cover" >
                <Text> Banner Container </Text>
            </ImageBackground>
            <View style={styles.loginContainer}>
                <View style={styles.headingContainer}>
                    <Text style={styles.headingText}>Welcome34</Text>
                </View>
                <View style={styles.registerContainer}>
                    <View style={{ flexDirection: 'row' }}>
                        <Text style={styles.registerText}>Don't have an account? </Text>
                        <TouchableOpacity onPress={() => { navigation.navigate('RegisterScreen') }}>
                            <Text style={{color: 'red'}}>Register</Text>
                        </TouchableOpacity>
                    </View>
                </View>
                <View style={styles.inputsContainer}>
                    <Input
                        label='Email'
                        placeholder='email@address.com'
                        leftIcon={{ type: 'font-awesome', name: 'envelope', size:24, marginRight: 7 }}
                        inputContainerStyle={{borderBottomColor: 'purple'}}
                        errorMessage='invalid email'
                    />
                    <Input
                        label='Password'
                        placeholder='password'
                        leftIcon={{ type: 'font-awesome', name: 'lock', size:24, marginRight: 7 }}
                        inputContainerStyle={{borderBottomColor: 'purple'}}
                        errorMessage='invalid password'
                    />
                </View>
                <View style={styles.buttonContainer}>
                    <TouchableOpacity style={styles.loginButton} onPress={() => { navigation.navigate('RegisterScreen') }}>
                        <Text style={styles.loginButtonText}>Log in</Text>
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    )
}
export default LoginScreen;

const styles = StyleSheet.create({
    mainContainer: {
        // backgroundColor: 'yellow',
        flex: 1,
    },
    bannerContainer: {
        backgroundColor: 'purple',
        opacity: 0.3,
        flex: 0.4,
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 1,
        },
        shadowOpacity: 0.22,
        shadowRadius: 2.22,
        elevation: 3,
    },
    bannerContainer_hide: {
        display: 'none'
    },
    loginContainer: {
        // backgroundColor: 'yellow',
        flex: 0.6,
    },
    headingContainer: {
        // backgroundColor: 'pink',
        flex: 0.2,
        justifyContent: 'flex-end',
        paddingLeft: '10%',
    },
    headingText: {
        fontSize: 30,
        fontWeight: 'bold',
        color: 'rgba(0,0,0,0.8)'
    },
    registerContainer: {
        // backgroundColor: 'purple',
        flex: 0.1,
        paddingLeft: '10%',
    },
    registerText: {
        fontSize: 14,
        fontWeight: '400',
        color: 'rgba(0,0,0,0.8)'
    },
    inputsContainer: {
        // backgroundColor: 'orange',
        flex: 0.4,
        justifyContent: 'space-around',
        paddingHorizontal: '10%'
    },
    buttonContainer: {
        // backgroundColor: 'green',
        flex: 0.3,
        justifyContent: 'center',
        alignItems: 'center'
    },
    loginButton: {
        width: '50%',
        height: '35%',
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 5,
        backgroundColor: 'purple',
    },
    loginButtonText: {
        color: 'white',
        fontSize: 20
    }
});
